    def greeter(self):
        """
            Приветственная надпись
        """
        print("Перед тобой супер-пупер безопасный сейф.")
        print("Твоя задача найти секретный ключ для его открытия, удачи!")
        print("")
    
    def verify_input(self, input_data):
        """
            Валидация ввода
            :param input_data: Введённый пользователем пин-код
            :returns: True - если ввод корректен
            :rtype: bool
        """
        if len(input_data) != 4:
            print("Пин должен быть из 4 цифр!")
            print()
            return False

        if not input_data.isdigit():
            print("Пин должен состоять только из цифр!")
            print()
            return False
        return True
    
    def verify_decode(self, data):
        """
            Валидация декодирования секрета
            :param data: Данные после попытки декодирования 
            :returns: True - если секрет успешно декодирован
            :rtype: bool
        """
        return (data[1:4] == b'PNG') and (data[-8:-4] == b'IEND')

    def __init__(self):
        """
            Запуск сейфа, спрашиваем пин, и сверяем результат декодирования с ожидаемым.
        """
        self.greeter()
        input_data = input("Введите сверх секретный пин-код из 4 цифр: ")
        
        if not self.verify_input(input_data):
            exit()
        
        # Пользовательский ввод в набор байт в кодировке utf-8
        encoded_input = input_data.encode("utf-8")

        # SHA256 от ввода пользователя
        hex_digest = self.hashlib.sha256(encoded_input).hexdigest()
        
        # hex_digest в виде байт
        key_bytes = hex_digest.encode("utf-8")

        # XOR-расшифровка секрета, cycle используется для повторения ключа, чтобы длина ключа была равна длине секрета
        result_bytes = bytes(encoded_data_byte ^ key_byte 
                             for encoded_data_byte, key_byte in 
                             zip(self.encoded_data, self.cycle(key_bytes)))
        
        # Проверка валидности расшифровки
        if self.verify_decode(result_bytes):
            print("Ура, сейф открыт!")
            print()

            # Вывод картинки
            image_buffer = self.io.BytesIO(result_bytes)
            img = self.mpimg.imread(image_buffer, format='png')
            self.plt.imshow(img)
            self.plt.axis("off")
            self.plt.title("Секретный код")
            self.plt.show()
        else:
            print("Обнаружена попытка взлома, сейф остаётся закрытым...")
            print()
            exit()
